impl ProfileManager {
    /// Extract MCP servers with name and enabled status
    pub fn extract_mcp_servers(&self, harness: &Harness, scope: Scope) -> Result<Vec<McpServerInfo>, String> {
        let servers = harness.parse_mcp_config(scope)?;
        Ok(servers.into_iter().map(|(name, server)| {
            McpServerInfo {
                name,
                enabled: match server {
                    McpServer::Stdio(s) => s.enabled.unwrap_or(true),
                    McpServer::Sse(s) => s.enabled.unwrap_or(true),
                    McpServer::Http(s) => s.enabled.unwrap_or(true),
                },
