```rust
pub fn show_profile(&self, harness: &Harness, profile_name: &str) -> Result<ProfileInfo> {
    let profile_path = self.get_profile_path(harness, profile_name)?;
    let mut extraction_errors = Vec::new();

    // Extract MCP servers with error capture
    let mcp_servers = match self.extract_mcp_servers(harness, Scope::Project) {
        Ok(servers) => servers,
        Err(e) => {
            extraction_errors.push(format!("MCP: {}", e));
            Vec::new()
        }
    };

    // Extract directory resources
    let skills = self.extract_skills(harness, Scope::Project);
    let commands = self.extract_commands(harness, Scope::Project);
    let plugins = self.extract_plugins(harness, Scope::Project);
    let agents = self.extract_agents(harness, Scope::Project);
    let rules_file = self.extract_rules_file(harness, Scope::Project);

    // Extract config values with error capture
    let theme = match self.extract_theme(harness, &profile_path) {
        Ok(t) => t,
        Err(e) => {
            extraction_errors.push(format!("Theme: {}", e));
            None
        }
    };

    let model = match self.extract_model(harness, &profile_path) {
        Ok(m) => m,
        Err(e) => {
            extraction_errors.push(format!("Model: {}", e));
            None
        }
    };

    Ok(ProfileInfo {
        name: profile_name.to_string(),
        harness_id: harness.kind().to_string(),
        is_active: self.is_active_profile(harness, profile_name)?,
        path: profile_path,
        mcp_servers,
        skills,
        commands,
        plugins,
        agents,
        rules_file,
        theme,
        model,
        extraction_errors,
    })
}
```

