use ratatui::{
    buffer::Buffer,
    layout::Rect,
    text::{Line, Span},
    widgets::{Paragraph, Widget},
};
use crate::tui::theme::Theme;

pub struct StatusBar<'a> {
    keybinds: &'a str,
    message: Option<&'a str>,
}

impl<'a> StatusBar<'a> {
    pub fn new(keybinds: &'a str) -> Self {
        Self { keybinds, message: None }
    }

    pub fn message(mut self, msg: Option<&'a str>) -> Self {
        self.message = msg;
        self
    }

    pub fn dashboard_keybinds() -> &'static str {
        "←→:harness  j/k:nav  Enter:switch  Space:details  n:new  d:del  e:edit  ?:help"
    }

    #[cfg(feature = "tui-cards")]
    pub fn cards_keybinds() -> &'static str {
        "←→:harness  j/k:card  Enter:switch  Space:details  n:new  d:del  ?:help"
    }
}

impl Widget for StatusBar<'_> {
    fn render(self, area: Rect, buf: &mut Buffer) {
        let spans = if let Some(msg) = self.message {
            vec![
                Span::styled(self.keybinds, Theme::STATUS_KEYBIND),
                Span::raw("  "),
                Span::styled(msg, Theme::STATUS_MESSAGE),
            ]
        } else {
            vec![Span::styled(self.keybinds, Theme::STATUS_KEYBIND)]
        };

        Paragraph::new(Line::from(spans)).render(area, buf);
    }
}
```

