use crate::cli::output::{ResolvedFormat, output_list};

pub fn list_profiles(harness: &str, format: ResolvedFormat) -> Result<()> {
    let harness_kind = HarnessKind::from_id(harness)?;
    let manager = ProfileManager::new(harness_kind);
    let profiles = manager.list()?;

    if profiles.is_empty() {
        // Empty case - still valid JSON
        output_list(&profiles, format, |_| {
            println!("No profiles found for {}", harness_kind.display_name());
        });
        return Ok(());
    }

    output_list(&profiles, format, |profiles| {
        println!("\nProfiles for {}:", harness_kind.display_name());
        println!("{:-<50}", "");
        for profile in profiles {
            let active = if profile.is_active { " (active)" } else { "" };
            println!("  • {}{}", profile.name, active);
        }
    });

    Ok(())
}
```

