use ratatui::{
    layout::{Constraint, Layout},
    Frame,
};
use crate::tui::{App, widgets::{HarnessTabs, CardGrid, StatusBar}};

pub struct CardView;

impl CardView {
    pub fn render(frame: &mut Frame, app: &mut App) {
        let [tabs_area, grid_area, status_area] = Layout::vertical([
            Constraint::Length(3),
            Constraint::Fill(1),
            Constraint::Length(1),
        ]).areas(frame.area());

        // Render harness tabs (shared widget)
        let tabs = HarnessTabs::new(
            &app.harnesses,
            app.harness_state.selected().unwrap_or(0),
            &app.bridle_config,
        );
        frame.render_widget(tabs, tabs_area);

        // Render card grid
        let grid = CardGrid::new(&app.profiles, app.card_selection);
        grid.render(frame, grid_area);

        // Render status bar
        let status = StatusBar::new(StatusBar::cards_keybinds())
            .message(app.status_message.as_deref());
        frame.render_widget(status, status_area);
    }
}
