### Implementation

```rust
use ratatui::{
    buffer::Buffer,
    layout::Rect,
    text::{Line, Span},
    widgets::{Block, Borders, Paragraph, Widget},
};
use crate::config::ProfileInfo;
use crate::tui::theme::Theme;

pub struct DetailPane<'a> {
    profile: Option<&'a ProfileInfo>,
    is_focused: bool,
}

impl<'a> DetailPane<'a> {
    pub fn new(profile: Option<&'a ProfileInfo>, is_focused: bool) -> Self {
        Self { profile, is_focused }
    }

    fn render_content(profile: &ProfileInfo) -> Vec<Line<'static>> {
        let mut lines = Vec::new();

        // Header
        lines.push(Line::styled(
            format!("Profile: {}", profile.name),
            Theme::SECTION_HEADER
        ));
        lines.push(Line::raw("─".repeat(30)));
        lines.push(Line::raw(""));

        // Model & Theme
        lines.push(Line::from(vec![
            Span::styled("Model: ", Theme::SECTION_HEADER),
            Span::styled(
                profile.model.as_deref().unwrap_or("—"),
                Theme::SECTION_CONTENT
            ),
        ]));

        if let Some(ref theme) = profile.theme {
            lines.push(Line::from(vec![
                Span::styled("Theme: ", Theme::SECTION_HEADER),
                Span::styled(theme.clone(), Theme::SECTION_CONTENT),
            ]));
        }
        lines.push(Line::raw(""));

        // MCP Servers
        lines.push(Line::styled(
            format!("MCP Servers ({})", profile.mcp_servers.len()),
            Theme::SECTION_HEADER
        ));

        let server_count = profile.mcp_servers.len();
        for (i, server) in profile.mcp_servers.iter().enumerate() {
            let branch = if i == server_count - 1 { "└─" } else { "├─" };
            let (marker, style) = if server.enabled {
                ("✓", Theme::MCP_ENABLED)
            } else {
                ("✗", Theme::MCP_DISABLED)
            };

            lines.push(Line::from(vec![
                Span::styled(format!(" {} ", branch), Theme::TREE_BRANCH),
                Span::styled(format!("{} {}", marker, server.name), style),
            ]));
        }

        if profile.mcp_servers.is_empty() {
            lines.push(Line::styled(" (none)", Theme::SECTION_CONTENT));
        }
        lines.push(Line::raw(""));

        // Resources (2-column layout)
        lines.push(Line::styled("Resources", Theme::SECTION_HEADER));

        let skills = profile.skills.items.len();
        let commands = profile.commands.items.len();
        lines.push(Line::from(vec![
            Span::raw(" Skills: "),
            Span::styled(format!("{:<4}", skills), Theme::SECTION_CONTENT),
            Span::raw(" Commands: "),
            Span::styled(format!("{}", commands), Theme::SECTION_CONTENT),
        ]));

        if let Some(ref plugins) = profile.plugins {
            let agents = profile.agents.as_ref().map(|a| a.items.len()).unwrap_or(0);
            lines.push(Line::from(vec![
                Span::raw(" Plugins: "),
                Span::styled(format!("{:<4}", plugins.items.len()), Theme::SECTION_CONTENT),
                Span::raw(" Agents: "),
                Span::styled(
                    if agents > 0 { format!("{}", agents) } else { "—".to_string() },
                    Theme::SECTION_CONTENT
                ),
            ]));
        }
        lines.push(Line::raw(""));

        // Rules file
        if let Some(ref rules) = profile.rules_file {
            if let Some(filename) = rules.file_name() {
                lines.push(Line::from(vec![
                    Span::styled("Rules: ", Theme::SECTION_HEADER),
                    Span::styled(filename.to_string_lossy().to_string(), Theme::SECTION_CONTENT),
                ]));
                lines.push(Line::raw(""));
            }
        }

        // Path (potentially wrapped)
        lines.push(Line::styled("Path:", Theme::SECTION_HEADER));
        let path_str = profile.path.display().to_string();
        // Split long paths across lines
        for chunk in path_str.as_bytes().chunks(35) {
            lines.push(Line::styled(
                format!(" {}", String::from_utf8_lossy(chunk)),
                Theme::SECTION_CONTENT
            ));
        }

        // Extraction errors (if any)
        if !profile.extraction_errors.is_empty() {
            lines.push(Line::raw(""));
            lines.push(Line::styled("⚠ Warnings", Style::default().fg(Color::Yellow)));
            for err in &profile.extraction_errors {
                lines.push(Line::styled(format!(" {}", err), Style::default().fg(Color::Yellow)));
            }
        }

        lines
    }
}

impl Widget for DetailPane<'_> {
    fn render(self, area: Rect, buf: &mut Buffer) {
        let border_style = if self.is_focused {
            Theme::BORDER_ACTIVE
        } else {
            Theme::BORDER_INACTIVE
        };

        let block = Block::default()
            .title(" Details ")
            .borders(Borders::ALL)
            .border_style(border_style);

        let content = match self.profile {
            Some(p) => Self::render_content(p),
            None => vec![Line::styled(
                "Select a profile to view details",
                Theme::SECTION_CONTENT
            )],
        };

        Paragraph::new(content)
            .block(block)
            .render(area, buf);
    }
}
```

## Widget 3: DashboardView Assembly

**File**: `src/tui/views/dashboard.rs`

```rust
use ratatui::{
    layout::{Constraint, Layout},
    Frame,
};
use crate::tui::{App, widgets::{HarnessTabs, ProfileTable, DetailPane, StatusBar}};

pub struct DashboardView;

impl DashboardView {
    pub fn render(frame: &mut Frame, app: &mut App) {
        let [tabs_area, main_area, status_area] = Layout::vertical([
            Constraint::Length(3),
            Constraint::Fill(1),
            Constraint::Length(1),
        ]).areas(frame.area());

        let [profiles_area, details_area] = Layout::horizontal([
            Constraint::Percentage(40),
            Constraint::Percentage(60),
        ]).areas(main_area);

        // Render harness tabs
        let tabs = HarnessTabs::new(
            &app.harnesses,
            app.harness_state.selected().unwrap_or(0),
            &app.bridle_config,
        );
        frame.render_widget(tabs, tabs_area);

        // Render profile table
        let table = ProfileTable::new(&app.profiles, true);
        frame.render_stateful_widget(table, profiles_area, &mut app.profile_state);

        // Render detail pane
        let selected_profile = app.profile_state.selected()
            .and_then(|i| app.profiles.get(i));
        let detail = DetailPane::new(selected_profile, false);
        frame.render_widget(detail, details_area);

        // Render status bar
        let status = StatusBar::new(StatusBar::dashboard_keybinds())
            .message(app.status_message.as_deref());
        frame.render_widget(status, status_area);
    }
}
```

